    API.logout();
    window.location = "/";
  };
  render() {
    return (
      <div className="Dashboard">
        <h1>Dashboard</h1>
        <img src="peon.jpg" alt=""/>
        <Button onClick={this.disconnect} block bsSize="large" type="submit">
